function reg($text){
        $regex = "[^aăâbcdđeêfghijklmnoôơpqrstuưvwxyzàằầèềìòồờùừỳáắấéếíóốớúứýảẳẩẻểỉỏổởủửỷãẵẫẽễĩõỗỡũữỹạặậẹệịọộợụựỵAĂÂBCDĐEÊFGHIJKLMNOÔƠPQRSTUƯVXYZÀẰẦÈỀÌÒỒỜÙỪỲÁẮẤÉẾÍÓỐỚÚỨÝẢẲẨẺỂỈỎỔỞỦỬỶÃẴẪẼỄĨÕỖỠŨỮỸẠẶẬẸỆỊỌỘỢỤỰỴ+\s]";

        $subject = mb_ereg_replace($regex,' ',$text);
        // $data = mb_ereg_replace("[^A-Za-z0-9\.\-]","",$data);
        return $subject;
    }
